SELECT AVG(CAST("Wins_4"."nFR1" AS double)) AS "avg:nFR1:ok",   AVG(CAST("Wins_4"."nFR2" AS double)) AS "avg:nFR2:ok",   AVG(CAST("Wins_4"."nFR3" AS double)) AS "avg:nFR3:ok" FROM "Wins_4" WHERE (("Wins_4"."tTRK3" NOT IN ('MD', 'RET', 'PLN', 'ONE', 'BCF', 'WRD', 'HST', 'HPO', 'PRV', 'ASD', 'CLS', 'FAR', 'TIL', 'GPR', 'ELK', 'ARP', 'SWF', 'EMD', 'CAS', 'CBY', 'SDY', 'ZIA', 'CWF', 'RIL', 'RP', 'BKF', 'FON', 'ALB', 'FER', 'SRP', 'RUI', 'DEP', 'ELY', 'GF', 'EMT', 'GIL', 'MED', 'ABT', 'PHA', 'BOI', 'OTC', 'UN', 'LNN', 'SUD', 'LBG', 'WYO', 'SON', 'MIL', 'FTP', 'GRP', 'FMT', 'CPW')) AND ("Wins_4"."tTRK3" = 'GP')) HAVING (COUNT(1) > 0);
